import React, { useEffect, useRef } from "react";

const MapWithAdvancedMarker = ({ lat, lng, apiKey }) => {
  const mapRef = useRef(null);
  const mapInstance = useRef(null);
  const markerInstance = useRef(null);

  useEffect(() => {
    if (!lat || !lng || !apiKey) return;

    // Check if Google Maps script is already loaded
    if (!window.google) {
      // Create script tag
      const script = document.createElement("script");
      script.src = `https://maps.googleapis.com/maps/api/js?key=${apiKey}`;
      script.async = true;
      script.defer = true;
      script.onload = () => initMap();
      document.head.appendChild(script);
    } else {
      initMap();
    }

    function initMap() {
      const location = { lat: Number(lat), lng: Number(lng) };

      // Create map if it doesn't exist
      if (!mapInstance.current) {
        mapInstance.current = new window.google.maps.Map(mapRef.current, {
          center: location,
          zoom: 12,
        });
      } else {
        mapInstance.current.setCenter(location);
      }

      // Remove old marker if exists
      if (markerInstance.current) {
        markerInstance.current.setMap(null);
      }

      // Create new AdvancedMarkerElement marker
      markerInstance.current = new window.google.maps.marker.AdvancedMarkerElement({
        position: location,
        map: mapInstance.current,
      });
    }
  }, [lat, lng, apiKey]);

  return <div ref={mapRef} style={{ width: "100%", height: "400px" }} />;
};

export default MapWithAdvancedMarker;
