import React, { useEffect, useRef ,useCallback} from "react";

const MapEmbed = ({ lat, lon }) => {
  const map_key = process.env.REACT_APP_GOOGLE_MAP_KEY;
console.log("Google API Key:", process.env.REACT_APP_GOOGLE_MAP_KEY);

  const mapRef = useRef(null);
  const initMap = useCallback(() => { 
    const location = { lat: Number(lat), lng: Number(lon) };
    const map = new window.google.maps.Map(mapRef.current, {
      zoom: 12,
      center: location,
    });
    
    new window.google.maps.Marker({
      position: location,
      map: map,
    });
  }, [lat, lon])
  
  
    useEffect(() => {
      if (!lat || !lon) return;
  
      // Load Google Maps script dynamically
      const existingScript = document.getElementById("googleMapsScript");
      if (!existingScript) {
        const script = document.createElement("script");
        script.id = "googleMapsScript";
        script.src = `https://maps.googleapis.com/maps/api/js?key=${map_key}`;
        script.async = true;
        script.defer = true;
        script.onload = initMap;
        document.body.appendChild(script);
      } else {
        initMap();
      }
    }, [lat, lon,initMap ,map_key]);

  return <div ref={mapRef} style={{ width: "100%", height: "400px" }} />;
};

export default MapEmbed;
